INSERT INTO `met_job` VALUES (1,'PHP技术支持',10,'长沙市','面议','2012-07-16',0,'<p>\r\n	<strong><span style=\"font-size:14px;\">主要工作内容：</span></strong></p>\r\n<div>\r\n	1. 负责MetInfo企业网站管理系统技术支持；</div>\r\n<div>\r\n	2. 为客服人员提供技术支持；</div>\r\n<div>\r\n	&nbsp;</div>\r\n<div>\r\n	<strong><span style=\"font-size:14px;\">岗位要求：&nbsp;</span></strong></div>\r\n<ol>\r\n	<li>\r\n		php能看懂且对PHP感兴趣，理解面向对象基本概念，写过一些小程序；</li>\r\n	<li>\r\n		懂MySql数据库备份、恢复等基本操作，熟悉PHP环境的搭建和配置；</li>\r\n	<li>\r\n		javascript能看懂，了解jquery等js框架；</li>\r\n	<li>\r\n		html/css会写，懂linux的优先；</li>\r\n	<li>\r\n		擅长网上查找资料解决问题；</li>\r\n	<li>\r\n		有PHP作品（留言板，blog等）的优先；</li>\r\n	<li>\r\n		做事要有耐心，性格谦和，学习能力强，能吃苦耐劳，愿意同公司共同发展。</li>\r\n</ol>\r\n<div>\r\n	如果你对我们的职位感兴趣，且符合我们的基本要求，请将个人简历投递至metinfo@qq.com，或者直接与我们取得联系！</div>\r\n',0,0,0,0,'','','cn'),(2,'网络销售',10,'长沙市','面议','2012-07-16',0,'<ol>\r\n	<li>\r\n		大专以上学历，一年以上网络销售经验；</li>\r\n	<li>\r\n		熟悉网络推广，熟悉网站建设基本流程；</li>\r\n	<li>\r\n		有网站制作相关工作经验者优先；</li>\r\n	<li>\r\n		学习能力强，能吃苦耐劳，愿意同公司共同发展；</li>\r\n	<li>\r\n		本岗位招收兼职，投递简历时请说明自己的工作意愿；</li>\r\n</ol>\r\n<div>\r\n	如果你对我们的职位感兴趣，且符合我们的基本要求，请将个人简历投递至metinfo@qq.com，或者直接与我们取得联系！</div>\r\n',0,0,0,0,'','','cn'),(3,'网页UI设计师',10,'长沙市','面议','2012-07-16',0,'<p>\r\n	<span style=\"font-size: 14px; \"><strong>主要工作内容：</strong></span>负责MetInfo界面和公司网站的界面设计等，重视用户体验。</p>\r\n<div>\r\n	<strong><span style=\"font-size:14px;\">岗位要求：</span></strong></div>\r\n<ol>\r\n	<li>\r\n		视觉设计、平面设计或美术相关专业，大专以上学历。</li>\r\n	<li>\r\n		具有良好的创意设计能力及良好的色彩感，有较高的美术功底，较强的网页设计能力和整体布局感。</li>\r\n	<li>\r\n		精通photoshop、Illustrator、Fireworks、Dreamweaver等图形设计工具中至少两种。</li>\r\n	<li>\r\n		了解网页交互设计知识，对作品有不断追求完美的精神特质。</li>\r\n	<li>\r\n		有网站UI设计同等职位工作经验、能提供过往作品者优先。</li>\r\n</ol>\r\n',0,0,0,0,'','','cn'),(4,'Web前端开发人员',10,'长沙市','面议','2012-07-16',0,'<div>\r\n	<strong><span style=\"font-size:14px;\">主要工作内容：</span></strong>负责MetInfo企业网站管理系统前台模板功能设计、用户体验提升和Web界面技术优化。</div>\r\n<div>\r\n	<strong><span style=\"font-size:14px;\">岗位要求：</span></strong></div>\r\n<ol>\r\n	<li>\r\n		熟悉JavaScript、Ajax、JQuery等前端开发脚本技能；</li>\r\n	<li>\r\n		精通HTML/XHTML、CSS，能够快速手写CSS代码，熟悉页面架构和布局。</li>\r\n	<li>\r\n		熟悉Web网站以及Web应用的可用性及用户分析方法，深入理解产品流程、用户体验和用户需求，对提升用户体验有一定经验及造诣。</li>\r\n	<li>\r\n		对Web技术发展有强烈兴趣，有良好的学习能力和强烈的进取心，愿意同公司共同发展。</li>\r\n	<li>\r\n		能跟踪最新的WEB前端设计、懂PHP基本语言者优先。</li>\r\n</ol>\r\n',0,0,0,0,'','','cn'),(5,'电子商务专员',10,'长沙市','面议','2012-07-16',0,'<p>\r\n	<strong><span style=\"font-size:14px;\">主要工作内容：</span></strong>负责MetInfo企业网站管理系统在线销售工作，向客户提供迅速、准确、周到的服务。</p>\r\n<div>\r\n	<strong><span style=\"font-size:14px;\">岗位要求：</span></strong></div>\r\n<ol>\r\n	<li>\r\n		<span style=\"font-size:14px;\">熟悉互联网销售与推广；</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">能熟练操作网站管理系统后台，了解基本的建站知识；</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">能敏锐把握网站建设行业市场动向，善于把握客户心理；</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">沟通能力强，勤奋耐劳，办事认真严谨细致；</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">具有较强的学习能力，愿意同公司共同发展；</span></li>\r\n	<li>\r\n		<span style=\"font-size:14px;\">有网站建设相关产品工作经验者优先，电子商务、市场营销等相关专业优先。</span></li>\r\n</ol>\r\n',0,0,0,0,'','','cn'),(6,'Internet sales',10,'ChangSha','Negotiable','2012-07-16',30,'<ol>\r\n	<li>\r\n		College education, more than one year of experience in network sales;</li>\r\n	<li>\r\n		Familiar with web promotion, familiar with the site construction process;</li>\r\n	<li>\r\n		Site production of relevant work experience is preferred;</li>\r\n	<li>\r\n		Ability to learn, hard working, willing and common development with the company;</li>\r\n	<li>\r\n		Positions to recruit part-time, please send your resume to explain their willingness to work;</li>\r\n</ol>\r\n<div>\r\n	If you are interested in our position and meet our basic requirements, please send your CV to metinfo@qq.com, or contact us directly!</div>\r\n',0,0,0,0,'','','en');
